          break;
        case SAI_WRED_ATTR_YELLOW_MIN_THRESHOLD:
          attr_list[i].value.u32 = attribute.value.u32;
          break;
        case SAI_WRED_ATTR_YELLOW_MAX_THRESHOLD:
