          break;
        case SAI_WRED_ATTR_RED_ENABLE:
          attr_list[i].value.booldata = attribute.value.booldata;
          break;
        case SAI_WRED_ATTR_RED_MIN_THRESHOLD:
          attr_list[i].value.u32 = attribute.value.u32;
          break;
        case SAI_WRED_ATTR_RED_MAX_THRESHOLD:
